// Ethereum Ticker -- Link to ethereum page, ticker and description
// Author: Samuel Adamson
import axios from 'axios';
import { Component } from 'react';
// Import React Elements
import { Row, Col, Container } from 'react-bootstrap';
import { NavLink } from 'react-router-dom';


class EthTick extends Component {
    // On component mount
    componentDidMount() {
        this.loadPrice();
        setInterval(this.loadPrice(), 6000);
    }

    // Get price from backend api
    async loadPrice() {
        axios
            .get('/ethPrice')
            .then(response => {
                this.setState({
                    price: response['data']
                });
            });
    }

    render() {
        return (
            <div className="EthTick">
                <Container className="ticker">
                    <NavLink exact to="/ethereum">
                        <Row>
                            <Col sm={8}>
                                <h1> Ethereum </h1>
                                <p>
                                    Ethereum is a decentralized, open-source blockchain with smart contract 
                                    functionality. Ether is the native cryptocurrency of the platform.
                                    Ethereum was created by Vitalik Buterin in 2013.
                                </p>
                            </Col>
                            <Col sm={4} className="tickerPrice my-auto">
                                <h2> ETH </h2>
                                <h2> ${this.state.price} </h2>
                            </Col>
                        </Row>
                    </NavLink>
                </Container>
            </div>
        );
    };
}

export default EthTick;
