// ETH Page -- Sparkline
// Author: Samuel Adamson
import axios from 'axios';
import { Component } from 'react';
import { Container, ToggleButtonGroup, ToggleButton } from 'react-bootstrap';
import Chart from 'react-apexcharts';

class EthSpark extends Component {
    constructor() {
        super();
        // Axios Promise
        this.axPromise = null;

        // State Values -- Chart Data/Configuration
        this.state = {
            // ApexCharts options
            options: {
                title: {
                    text: 'ETH',
                    align: 'left',
                    margin: 10,
                    offsetX: 0,
                    offsetY: 0,
                    floating: false,
                    style: {
                        fontSize:  '75px',
                        fontWeight:  'bold',
                        color:  '#802bb1'
                    },
                },
                subtitle: {
                    text: '4000',
                    align: 'left',
                    margin: 10,
                    offsetX: 0,
                    offsetY: 75,
                    floating: false,
                    style: {
                        fontSize:  '60px',
                        fontWeight:  'normal',
                        color:  '#fff'
                    },
                },
                chart: {
                    type: 'area',
                    stacked: false,
                    toolbar: {
                        show: false
                    }
                },
                stroke: {
                    show: true,
                    curve: 'smooth',
                    colors: '#802bb1'
                },
                xaxis: {
                    show: false,
                    type: 'numeric',
                    labels: {
                        show: false
                    },
                    axisTicks: {
                        show: false
                    }

                },
                yaxis: {
                    show: false
                },
                grid: {
                    show: false
                },
                noData: {
                    text: 'Loading...',
                    align: 'center',
                    verticalAlign: 'middle',
                    offsetX: 0,
                    offsetY: 0,
                    style: {
                        color: '#802bb1',
                        fontSize: '7rem'
                    }
                }
            },
            series: []
        };
    };

    // Get Data from backend
    storeData() {
        // Make request
        const promise = axios.get('/ethSpark');
        // Store promise result
        this.axPromise = promise.then((response) => response.data);
    }

    // Load Chart Timeseries with 1 Day Data
    loadDay() {
        // Access promise data
        this.axPromise.then(body => {
            // Check not null
            if(body) {
                // Store daydata in chart series
                this.setState({
                    series: [{
                        name: 'Ethereum',
                        data: body['day']
                    }]
                });
            } else { // Null Response
                console.log('Null Response!');
            }

        })
        .catch(function(error) {
            console.log(error);
        });
    }

    // On Component Mount
    componentDidMount = () => {
        this.storeData(); // Store Data for Sparklines
        this.loadDay();   // Load one day data by default
        console.log(this.state.series);
    };

    render() {
        return(
            <div className="EthSpark spark">
                <Container className="pt-5 pb-5">
                    <ToggleButtonGroup type="radio" name="options" defaultValue={0}>
                        <ToggleButton id="tbg-radio-0" size="lg" value={0}> <strong> 1D </strong> </ToggleButton>
                        <ToggleButton id="tbg-radio-1" size="lg" value={1}> <strong> 1W </strong> </ToggleButton>
                        <ToggleButton id="tbg-radio-2" size="lg" value={2}> <strong> 1M </strong> </ToggleButton>
                        <ToggleButton id="tbg-radio-3" size="lg" value={3}> <strong> 1Y </strong> </ToggleButton>
                    </ToggleButtonGroup>

                    <Chart 
                        options={this.state.options}
                        series={this.state.series}
                        type="line"
                        width="100%" 
                    />
                </Container>
            </div>
        );
    }
}

export default EthSpark;
