// ETH Page -- Sparkline
// Author: Samuel Adamson
import axios from 'axios';
import { Component } from 'react';
import { Container, ToggleButtonGroup, ToggleButton } from 'react-bootstrap';
import { Line } from 'react-chartjs-2';

class EthSpark extends Component {
    constructor() {
        super();
        this.state = {
            // Data from API
            dayData: {},
            weekData: {},
            monthData: {},
            yearData: {},
            // Chart Data -- used in final
            chartData: {}, 
            // Options -- Configuration
            chartOps: {
                responsive: true,
                legend: { display: false },
                elements: {
                    line: {
                        borderColor: 'rgba(128, 43, 177, 1)',
                        borderWidth: 2
                    },
                    point: { radius: 1 }
                },
                tooltips: { enabled: false },
                scales: {
                    yAxes: [{ 
                        display: false,
                        gridLines: { display: false }
                    }],
                    xAxes: [{ 
                        display: false,
                        gridLines: { display: false }
                    }]
                }
            }
        };
    };

    // Get Data from backend
    storeData() {
        // Make request
        axios
            .get('/ethSpark')
            .then(response => {
                // Story response body
                let body = response['data'];

                // Check not null
                if(body) {

                    // Store day, week, month, year data
                    this.setState({
                        dayData: {
                            labels: body['day']['labels'],
                            datasets: [{
                                label: 'Dataset',
                                data: body['day']['data'],
                                fill: false,
                                backgroundColor: 'transparent',
                                borderColor: 'rgba(128, 43, 177, 1)'
                            }]
                        },
                    }); 

                } else {
                    console.log('Null Response!');
                    this.setState({
                        // Set all to null
                        dayData: null, weekData: null, monthData: null, yearData: null,
                        chartData: null, chartOps: null
                    });   
                }

            })
            .catch( function(error) {
                // Log Error
                console.log(error);
                this.setState({
                    // Set all to null
                    dayData: null, weekData: null, monthData: null, yearData: null,
                    chartData: null, chartOps: null
                });
            });
    }

    // Chart Data
    chartData(type) {
        if(type === 'day') {
            // Set state
            this.setState((state) => ({ chartData: state.dayData }));
        }
    }

    // On Component Mount
    componentDidMount = () => {
        this.storeData(); // Store Data for Sparklines
        //this.chartData('day'); // Chart Data (First time)
    };

    render() {
        return(
            <div className="EthSpark spark">
                <Container>
                    <ToggleButtonGroup type="radio" name="options" defaultValue={0}>
                        <ToggleButton id="tbg-radio-0" size="lg" value={0}> <strong> 1D </strong> </ToggleButton>
                        <ToggleButton id="tbg-radio-1" size="lg" value={1}> <strong> 1W </strong> </ToggleButton>
                        <ToggleButton id="tbg-radio-2" size="lg" value={2}> <strong> 1M </strong> </ToggleButton>
                        <ToggleButton id="tbg-radio-3" size="lg" value={3}> <strong> 1Y </strong> </ToggleButton>
                    </ToggleButtonGroup>

                    <Line data={this.state.chartData} options={this.state.chartOps} />
                </Container>
            </div>
        );
    }
}

export default EthSpark;
