// ETH Page -- Sparkline
// Author: Samuel Adamson
import axios from 'axios';
import { Component } from 'react';
import { Container, ToggleButtonGroup, ToggleButton } from 'react-bootstrap';
import { Line } from 'react-chartjs-2';

class EthSpark extends Component {
    constructor() {
        super();

        // Store requests from API
        this.dayData = 'nothing';
        this.weekData = {};
        this.monthData = {};
        this.yearData = {};


        // State Values -- Chart Configuration
        this.state = {
            // Chart Data -- used in final
            chartData: {}
        };
    };

    // Get Data from backend
    storeData() {
        // Make request
        axios
            .get('/ethSpark')
            .then(response => {
                // Story response body
                let body = response['data'];

                // Check not null
                if(body) {

                    // Store day, week, month, year data
                    this.dayData = {
                            labels: body['day']['labels'],
                            datasets: [{
                                data: body['day']['data'],
                                fill: false,
                                backgroundColor: 'transparent',
                                borderColor: 'rgba(128, 43, 177, 1)',
                            }]
                    }

                    this.setState({
                        chartData: this.dayData
                    })

                } else {
                    console.log('Null Response!');
                    this.setState({
                        // Set all to null
                        dayData: null, weekData: null, monthData: null, yearData: null,
                        chartData: null, chartOps: null
                    });   
                }

            })
            .catch( function(error) {
                // Log Error
                console.log(error);
                this.setState({
                    // Set all to null
                    dayData: null, weekData: null, monthData: null, yearData: null,
                    chartData: null, chartOps: null
                });
            });
    }

    // On Component Mount
    componentDidMount = () => {
        this.storeData(); // Store Data for Sparklines
    };

    render() {
        return(
            <div className="EthSpark spark">
                <Container>
                    <ToggleButtonGroup type="radio" name="options" defaultValue={0}>
                        <ToggleButton id="tbg-radio-0" size="lg" value={0}> <strong> 1D </strong> </ToggleButton>
                        <ToggleButton id="tbg-radio-1" size="lg" value={1}> <strong> 1W </strong> </ToggleButton>
                        <ToggleButton id="tbg-radio-2" size="lg" value={2}> <strong> 1M </strong> </ToggleButton>
                        <ToggleButton id="tbg-radio-3" size="lg" value={3}> <strong> 1Y </strong> </ToggleButton>
                    </ToggleButtonGroup>

                    <Line data={this.state.chartData} options={this.chartOps} />
                </Container>
            </div>
        );
    }
}

export default EthSpark;
